mapper.update:
UPDATE enroll
SET enroll_status = 2
WHERE s_idx = #{s_idx}
  AND lect_idx = #{lect_idx}
  AND enroll_status = 0

mapper.select:
SELECT enroll_status
FROM enroll
WHERE s_idx = #{s_idx}
  AND lect_idx = #{lect_idx}

mapper.update:
UPDATE enroll
SET enroll_status = 0,
    enroll_time = NOW()
WHERE s_idx = #{s_idx}
  AND lect_idx = #{lect_idx}
